import React, { Fragment, Component } from 'react';
import Breadcrumb from '../../common/breadcrumb';
import { ToastContainer, toast } from 'react-toastify';

class BulkUploadAsset extends Component{
    constructor(props){

        super(props)
        this.state = {
                 selectedFile: null
                }
    }

    onFileChangeHandler = (e) => {
        e.preventDefault();
        this.setState({
            selectedFile: e.target.files[0]},()=>{
                const formData = new FormData();
                formData.append('file', this.state.selectedFile);
                fetch('/File/upload', {
                    method: 'post',
                    body: formData,

                }).then(res => {
                    if(res.ok) {
                        console.log(res.data);
                        toast.success("File uploaded successfully.")
                    }
                });
            }
        );

    };

    fileUploadHandler = () => {
        const data = new FormData()
        console.log(this.state.selectedFile);
        data.append('file', this.state.selectedFile)
        console.log(data);
        fetch('/File/upload', {
            method: 'post',
            body: formData,

        }).then(res => {
            if(res.ok) {
                console.log(res.data);
                toast.success("File uploaded successfully.")
            }
        });

      };

    validateSize=(event)=>{
        let file = event.target.files[0];
        let size = 30000;
        let err = '';
        console.log(file.size);
        if (file.size > size) {
         err = file.type+'is too large, please pick a smaller file\n';
         toast.error(err);
       }
       return true
      };

render (){
    return (
        <Fragment>
            <Breadcrumb parent="Inwarding" title="Bulk Upload Assets" />
            <div className="container-fluid">
                <div className="row">
                    <div className="col-sm-12">
                    <ToastContainer />
                        <div className="card">
                            <div className="card-header">
                                <h5>Bulk Upload Assets</h5>
                            </div>
                            <div className="card-body">
                               <div className="container">
                                    <div className="row">
                                        <div className="col-md-6">
                                                <div className="form-group files color">
                                                    <label>Upload Your File </label>
                                                    <input type="file" className="form-control" name="file" onChange={this.onFileChangeHandler}/>
                                                </div>
                                                <div className="col-md-6 pull-right">
                                                   <button width="100%" type="button" className="btn btn-info" onClick={this.fileUploadHandler}>Upload File</button>
                                                </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </Fragment>
    )
}

}

export default BulkUploadAsset;
