import React from 'react';
import PieChart, {
  Legend,
  Export,
  Series,
  Label,
  Font,
  Connector
} from 'devextreme-react/pie-chart';

class DevPieChart extends React.Component {
    constructor(props){
        super(props)
        this.dataSource = [{
                country: 'Tables',
                medals: 1864
            }, {
                country: 'Cots',
                medals: 576
            }, {
                country: 'Cpu',
                medals: 262
            }, {
                country: 'Refrigerator',
                medals: 236
            }, {
                country: 'Cabinets',
                medals: 1076
            }, {
                country: 'Artwork',
                medals: 476
            }, {
                country: 'Chairs',
                medals: 1446
            }, {
                country: 'Benches',
                medals: 299
            }];
    }

  render() {
    return (
      <PieChart id="pie"
        palette="Bright"
        dataSource={this.dataSource}
        title="Olympic Medals in 2008"
      >
        <Legend
          orientation="horizontal"
          itemTextPosition="right"
          horizontalAlignment="center"
          verticalAlignment="bottom"
          columnCount={4} />
        <Export enabled={true} />
        <Series argumentField="country" valueField="medals">
          <Label
            visible={true}
            position="columns"
            customizeText={customizeText}>
            <Font size={16} />
            <Connector visible={true} width={0.5} />
          </Label>
        </Series>
      </PieChart>
    );
  }
}

function customizeText(arg) {
  return `${arg.valueText} (${arg.percentText})`;
}

export default DevPieChart;
